import React from "react";
import { Helmet } from "react-helmet-async";
import "./CurrencyConversion.scss";

class CurrencyConversion extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      amount1: 0,
      amount2: 0,
      height: "",
      weight: "",
      rate: 0,
    };
    this.convertCurrency = this.convertCurrency.bind(this);
  }

  // componentDidMount() {
  //   fetch(`https://api.coinmarketcap.com/v1/ticker/?limit=10`)
  //     .then(res => res.json())
  //     .then(json => this.setState({ data: json }));
  // }

  convertCurrency = (from, to) => {
    debugger;
    fetch(
      `https://api.exchangeratesapi.io/latest?base=${from}`
    )
      .then((res) => res.json())
      .then((json) => this.setState({ rate: json.rates[to] }));
    this.setState({ amount2: this.state.amount1 * this.staterate });
  };

  render() {
    return (
      <>
        <Helmet>
          <title>Shop</title>
          <meta name="description" content="Shop our latest products now." />
          <link rel="canonical" href="/shop" />
        </Helmet>
        <div>
          <h1>تحويل عملات</h1>
          <input
            onChange={(e) => this.setState({ amount1: e.target.value })}
          ></input>
          <button onClick={this.convertCurrency("USD", "EUR")}>submit</button>
          {this.amount2}
        </div>
      </>
    );
  }
}

export default CurrencyConversion;
