import React from "react";

import { Helmet } from "react-helmet-async";
import "./CurrencyConversion.scss";

class CurrencyConversion extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      amount1: 0,
      amount2: 0,
      height: "",
      weight: "",
    };
    this.convertCurrency = this.convertCurrency.bind(this);
  }


convertCurrency(amount, from, to) {
    debugger
    const response = fetch(`https://api.exchangeratesapi.io/latest?base=${from}`);
    const data = response.json();
    const rate = data.rates[to];
    this.setState({ amount2: amount * rate})
  }


  render() {
  return (
    <>
      <Helmet>
        <title>Shop</title>
        <meta name="description" content="Shop our latest products now." />
        <link rel="canonical" href="/shop" />
      </Helmet>
      <div>
        <h1>تحويل عملات</h1>
        <input onChange={e => this.setState({ amount1: e.target.value })}></input>
        <button onClick={this.convertCurrency(this.amount1, 'USD', 'EUR')}>submit</button>
        {this.amount2}
      </div>
    </>
  );
  }
};

export default CurrencyConversion;
