import { useState, useEffect } from "react";
import { Helmet } from "react-helmet-async";
import "./CurrencyConversion.scss";

const CurrencyConversion = () => {
  const [amount1, setAmount1] = useState(1);
  const [amount2, setAmount2] = useState(1);
  const [currency1, setCurrency1] = useState("USD");
  const [currency2, setCurrency2] = useState("EUR");
  const [rates, setRates] = useState([]);

  async function convertCurrency(amount, from, to) {
    const response = await fetch(`https://api.exchangeratesapi.io/latest?base=${from}`);
    const data = await response.json();
    const rate = data.rates[to];
    return amount * rate;
  }

  convertCurrency(currency1, 'USD', 'EUR').then(result => {
    setAmount1(result)
  });


  return (
    <>
      <Helmet>
        <title>Shop</title>
        <meta name="description" content="Shop our latest products now." />
        <link rel="canonical" href="/shop" />
      </Helmet>
      <div>
        <h1>تحويل عملات</h1>
        <input onChange={e => setCurrency1(e.target.value)}></input>
        <button onClick={this.convertCurrency(currency1, 'USD', 'EUR')}>submit</button>
        {amount1}
      </div>
    </>
  );
};

export default CurrencyConversion;
