  //     });
  // }

  convertCurrency = (from, to) => {
    var myHeaders = new Headers();
    myHeaders.append("apikey", "T7nKfWqqYTSJgAzMzekYNBS7bFMSkN2C");

    var requestOptions = {
      method: "GET",
      redirect: "follow",
      headers: myHeaders,
    };

    fetch(
      `https://api.apilayer.com/exchangerates_data/convert?to=${to}&from=${from}&amount=${this.state.amount1}`,
      requestOptions
    )
      .then((response) => response.text())
      .then((result) => {
        console.log(result);
        this.setState({ amount2: this.result.result });
      })
      .catch((error) => console.log("error", error));
    // debugger;
    // const data = fetch(
