    //   & amount = 25`
    // ).then((res) => res.json());
    // const rate = data.rates[to];
    // this.setState({ amount2: this.state.amount1 * rate });
  };

  render() {
    return (
      <>
        <Helmet>
          <title>Shop</title>
          <meta name="description" content="Shop our latest products now." />
          <link rel="canonical" href="/shop" />
        </Helmet>
        <div>
          <h1>تحويل عملات</h1>
          <input
            onChange={(e) => this.setState({ amount1: e.target.value })}
          ></input>
          <button onClick={this.convertCurrency("USD", "EUR")}>submit</button>
          {this.amount2}
        </div>
      </>
    );
  }
}

export default CurrencyConversion;
