import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import ScientificCalculator from "./pages/scientific calculator/ScientificCalculator";
import Calculator from "./pages/calculator/Calculator";
import IvestmentCalculator from "./pages/investment calculator/InvestmentCalculator";
import BmiCalculator from "./pages/bmi calculator/BmiCalculator";
import AgeCalculator from "./pages/age calculator/AgeCalculator";
import NetSpeed from "./pages/net speed/NetSpeed";
import CurrencyConversion from "./pages/currency convertor/CurrencyConversion"

import NavBar from "./cmps/navbar/Navbar";

import "./App.css";

export default function App() {
  const [state, setstate] = useState("Calculator");

  return (
    <div className="main">
      <NavBar />
      <BrowserRouter>
      <Routes>
        <Route path="/" element={<Calculator />}>
          <Route path="Calculator" element={<Calculator />} />
          <Route path="ScientificCalculator" element={<ScientificCalculator />} />
          <Route path="IvestmentCalculator" element={<IvestmentCalculator />} />
          <Route path="BmiCalculator" element={<BmiCalculator />} />
          <Route path="AgeCalculator" element={<AgeCalculator />} />
          <Route path="NetSpeed" element={<NetSpeed />} />
          <Route path="CurrencyConversion" element={<CurrencyConversion />} />
        </Route>
      </Routes>
    </BrowserRouter>
      <h1> آلة حاسبة </h1>
      <div className="content">
        <nav className="navbar">
          <button onClick={() => setstate("Calculator")}>آلة حاسبة</button>
          <button onClick={() => setstate("ScientificCalculator")}>آلة حاسبة علمية</button>
          <button onClick={() => setstate("IvestmentCalculator")}>حاسبة الاستثمار</button>
          <button onClick={() => setstate("BmiCalculator")}>حاسبة مؤشر كتلة الجسم BMI</button>
          <button onClick={() => setstate("AgeCalculator")}>حساب العمر</button>
          <button onClick={() => setstate("NetSpeed")}> قياس سرعة الإنترنت</button>
          <button onClick={() => setstate("CurrencyConversion")}> تحويل عملات</button>
        </nav>
        {state === "Calculator" && <Calculator />}
        {state === "ScientificCalculator" && <ScientificCalculator />}
        {state === "IvestmentCalculator" && <IvestmentCalculator />}
        {state === "BmiCalculator" && <BmiCalculator />}
        {state === "AgeCalculator" && <AgeCalculator />}
        {state === "NetSpeed" && <NetSpeed />}
        {state === "CurrencyConversion" && <CurrencyConversion />}
      </div>
    </div>
  );
}
