import React from "react";
import ScientificCalculator from './cmps/scientific calculator/ScientificCalculator';


class App extends React.Component {
  state = {
    name: "",
  };

  componentDidMount() {
    fetch("http://localhost:3000")
      .then((res) => res.json())
      .then((data) => this.setState({ name: data.name }));
  }

  render() {
    return (
      <div className="main">
        <h1> آلة حاسبة </h1>
        <ScientificCalculator />
      </div>
    );
  }
}

export default App;
