import React, { useState } from "react";
import ScientificCalculator from "./cmps/scientific calculator/ScientificCalculator";
import Calculator from "./cmps/calculator/Calculator";
import IvestmentCalculator from "./cmps/investment calculator/InvestmentCalculator";
import BmiCalculator from "./cmps/bmi calculator/BmiCalculator";
import AgeCalculator from "./cmps/age calculator/AgeCalculator";
import NetSpeed from "./cmps/net speed/NetSpeed";


import "./App.css";

export default function App() {
  const [state, setstate] = useState("Calculator");

  return (
    <div className="main">
      <h1> آلة حاسبة </h1>
      <div className="content">
        <nav className="navbar">
          <button onClick={() => setstate("Calculator")}>آلة حاسبة</button>
          <button onClick={() => setstate("ScientificCalculator")}>آلة حاسبة علمية</button>
          <button onClick={() => setstate("IvestmentCalculator")}>حاسبة الاستثمار</button>
          <button onClick={() => setstate("BmiCalculator")}>حاسبة مؤشر كتلة الجسم BMI</button>
          <button onClick={() => setstate("AgeCalculator")}>حساب العمر</button>
          <button onClick={() => setstate("NetSpeed")}> قياس سرعة الإنترنت</button>
        </nav>
        {state === "Calculator" && <Calculator />}
        {state === "ScientificCalculator" && <ScientificCalculator />}
        {state === "IvestmentCalculator" && <IvestmentCalculator />}
        {state === "BmiCalculator" && <BmiCalculator />}
        {state === "AgeCalculator" && <AgeCalculator />}
        {state === "NetSpeed" && <NetSpeed />}
      </div>
    </div>
  );
}
